	@Test
	public void reciclaProductoEnQuioscoConFoto() throws JSONException {
		String api = "/reciclaje/recicla-producto-quiosco";
		String token = getTokenLogin();
		
		ReciclaProductoQuioscoResponseDTO dto = new ReciclaProductoQuioscoResponseDTO();		
		dto.setIdProducto(175L);
		dto.setIdQuiosco(19L);
		dto.setIdUsuario(113577L);
		dto.setFoto(this.image);
		dto.setCodigoRespuesta(0);
		
		given()
			.headers("Authorization", "Bearer " + token)
			.body(dto)
			.contentType(MediaType.APPLICATION_JSON_VALUE)			
			.when()
				.post(api)
			.then()
				.statusCode(equalTo(201))
				.contentType(MediaType.APPLICATION_JSON_VALUE);
			
		System.out.println("Test finalizado: reciclaProductoQuioscoConFoto()");	
	}
	
